import pandas as pd

# Amino acid sequence
protein_seq = ""

# DNA sequence (clean + uppercase)
dna_seq = """""".upper()

# Remove non-ATGC characters (if any)
dna_seq = ''.join([base for base in dna_seq if base in "ATGC"])

# Split DNA into codons
codons = [dna_seq[i:i+3] for i in range(0, len(protein_seq)*3, 3)]

# Create DataFrame
df = pd.DataFrame({
    "Position": range(1, len(protein_seq) + 1),
    "Amino Acid": list(protein_seq),
    "Codon": codons
})

print(df)
# Save to CSV
df.to_csv("amino_acid_codon_table.csv", index=False)

